              --bs-gray-400: #aab0bc;
              --bs-gray-500: #959ca9;
              --bs-gray-600: #60697b;
              --bs-gray-700: #2f353a;
              --bs-gray-800: #21262c;
              --bs-gray-900: #1e2228;
              --bs-blue: #5271ff;
              --bs-primary: #5271ff;
              --bs-white: #fff;
            }
            * {
              margin: 0;
              padding: 0;
              box-sizing: border-box;
            }
            body {
              font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
              background-color: var(--bs-gray-200);
              color: var(--bs-gray-900);
