const techOptions = [
  { name: "Python", count: 11 },
  { name: "SQL", count: 10 },
  { name: "Java", count: 6 },
  { name: "Spring Boot", count: 5 },
  { name: "Type script", count: 5 }
];

const locationOptions = [
  { name: "Worldwide", count: 29 },
  { name: "World Wide", count: 24 },
  { name: "India", count: 5 },
  { name: "world wide", count: 4 },
  { name: "worldwide", count: 1 }
];

const allTechOptions = [
  { name: "Python", count: 11 },
  { name: "Spring Boot", count: 5 },
  { name: ".NET", count: 4 },
  { name: "JavaScript", count: 4 },
  { name: "Node js", count: 3 },
  { name: "Azure", count: 3 },
  { name: "Snowflake", count: 2 },
  { name: "Tableau", count: 2 },
  { name: "REST APIs", count: 2 },
  { name: "SQL", count: 10 },
  { name: "Type script", count: 5 },
  { name: "Java Script", count: 4 },
  { name: "Angular", count: 3 },
  { name: "FrontEnd", count: 3 },
  { name: "Full Stack Development", count: 3 },
  { name: "POWER BI", count: 2 },
  { name: "API", count: 2 },
  { name: "LLM", count: 2 },
  { name: "Java", count: 6 },
  { name: "React", count: 5 },
  { name: "Oracle", count: 4 },
  { name: "REST", count: 3 },
  { name: "Apex", count: 3 },
  { name: "C#", count: 2 },
  { name: "Multi Threading", count: 2 },
  { name: "Azure Cloud", count: 2 },
  { name: "Github", count: 2 }
];

const industries = [
  "Travel",
  "Automotive", 
  "Banking",
  "Capital Markets",
  "Healthcare",
  "Digital Commerce",
  "View all"
];

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [dateFilter, setDateFilter] = useState('all');
  const [selectedTech, setSelectedTech] = useState([]);
  const [selectedLocations, setSelectedLocations] = useState([]);
  const [minExperience, setMinExperience] = useState('0');
  const [maxExperience, setMaxExperience] = useState('30');
  const [showModal, setShowModal] = useState(false);
  const [techSearch, setTechSearch] = useState('');
  const [locationSearch, setLocationSearch] = useState('');
  const [visibleJobs, setVisibleJobs] = useState(6);

  const handleTechChange = (tech) => {
    setSelectedTech(prev => 
      prev.includes(tech) 
        ? prev.filter(t => t !== tech)
        : [...prev, tech]
    );
  };

  const handleLocationChange = (location) => {
    setSelectedLocations(prev => 
      prev.includes(location) 
        ? prev.filter(l => l !== location)
        : [...prev, location]
    );
  };

  const clearAllFilters = () => {
    setSelectedTech([]);
    setSelectedLocations([]);
    setDateFilter('all');
    setMinExperience('0');
    setMaxExperience('30');
    setTechSearch('');
    setLocationSearch('');
    setSearchTerm('');
  };

  const filteredTechOptions = allTechOptions.filter(tech =>
    tech.name.toLowerCase().includes(techSearch.toLowerCase())
  );

  const shareJob = (jobTitle) => {
    if (navigator.share) {
      navigator.share({
        title: jobTitle,
        text: `Check out this job: ${jobTitle}`,
        url: window.location.href
      });
    } else {
      navigator.clipboard.writeText(`${jobTitle} - ${window.location.href}`);
      alert('Job link copied to clipboard!');
    }
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const showMoreJobs = () => {
    setVisibleJobs(prev => Math.min(prev + 4, filteredJobs.length));
  };

  const getExperienceNumber = (expStr) => {
    const match = expStr.match(/(\d+)/);
    return match ? parseInt(match[1]) : 0;
  };

  const filteredJobs = allJobs.filter(job => {
    // Search filter
    const matchesSearch = searchTerm === '' || 
      job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      job.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()));

    // Date filter
    const matchesDate = dateFilter === 'all' || 
      (dateFilter === 'past3days' && job.datePosted <= 3) ||
      (dateFilter === 'pastweek' && job.datePosted <= 7) ||
      (dateFilter === 'pastmonth' && job.datePosted <= 30);

    // Technology filter
    const matchesTech = selectedTech.length === 0 || 
      selectedTech.some(tech => 
        job.tags.some(tag => 
          tag.toLowerCase().includes(tech.toLowerCase()) ||
          tech.toLowerCase().includes(tag.toLowerCase())
        )
      );

    // Location filter
    const matchesLocation = selectedLocations.length === 0 ||
      selectedLocations.some(loc => 
        job.location.toLowerCase().includes(loc.toLowerCase())
      );

    // Experience filter
    const jobExp = getExperienceNumber(job.experience);
    const minExp = parseInt(minExperience);
    const maxExp = parseInt(maxExperience);
    const matchesExperience = jobExp >= minExp && jobExp <= maxExp;

    return matchesSearch && matchesDate && matchesTech && matchesLocation && matchesExperience;
  });

  const displayedJobs = filteredJobs.slice(0, visibleJobs);

  return (
    <div>
      <header className="header">
        <div className="header-content">
          <div className="logo">AugmntX</div>
          <nav className="nav">
            <a href="#" className="nav-link">Why</a>
            <div className="industries-dropdown">
              <a href="#" className="nav-link">Industries </a>
              <div className="dropdown-menu">
                {industries.map(industry => (
                  <a key={industry} href="#" className="dropdown-item">{industry}</a>
                ))}
              </div>
            </div>
            <a href="#" className="nav-link">Find Dev</a>
            <a href="#" className="nav-link">Remote Jobs</a>
            <a href="#" className="hire-btn">Hire Dev</a>
            <a href="#" className="nav-link">Login</a>
          </nav>
        </div>
      </header>

      <div className="main-container">
        <aside className="sidebar">
          <div className="jobs-count">{filteredJobs.length} jobs</div>

          <div className="filter-section">
            <h3 className="filter-title">Sort & Filter</h3>

            <div className="date-filter">
              <h4> Date Posted</h4>
              <div className="filter-options">
                <div className="filter-option">
                  <input 
                    type="radio" 
                    id="past3days" 
                    name="dateFilter" 
                    value="past3days"
                    checked={dateFilter === 'past3days'}
                    onChange={(e) => setDateFilter(e.target.value)}
                  />
                  <label htmlFor="past3days">Past 3 Days</label>
                </div>
                <div className="filter-option">
                  <input 
                    type="radio" 
                    id="pastweek" 
                    name="dateFilter" 
                    value="pastweek"
                    checked={dateFilter === 'pastweek'}
                    onChange={(e) => setDateFilter(e.target.value)}
                  />
                  <label htmlFor="pastweek">Past Week</label>
                </div>
                <div className="filter-option">
                  <input 
                    type="radio" 
                    id="pastmonth" 
                    name="dateFilter" 
                    value="pastmonth"
                    checked={dateFilter === 'pastmonth'}
                    onChange={(e) => setDateFilter(e.target.value)}
                  />
                  <label htmlFor="pastmonth">Past Month</label>
                </div>
                <div className="filter-option">
                  <input 
                    type="radio" 
                    id="all" 
                    name="dateFilter" 
                    value="all"
                    checked={dateFilter === 'all'}
                    onChange={(e) => setDateFilter(e.target.value)}
                  />
                  <label htmlFor="all">All</label>
                </div>
              </div>
            </div>

            <div className="filter-section-divider"></div>

            <div className="tech-filter">
              <h4> Technology / Tools</h4>
              <input
                type="text"
                className="tech-search"
                value={techSearch}
                onChange={(e) => setTechSearch(e.target.value)}
              />
              {techOptions.map(tech => (
                <div key={tech.name} className="tech-option">
                  <div style={{display: 'flex', alignItems: 'center'}}>
                    <input 
                      type="checkbox" 
                      id={tech.name}
                      checked={selectedTech.includes(tech.name)}
                      onChange={() => handleTechChange(tech.name)}
                    />
                    <label htmlFor={tech.name}>{tech.name}</label>
                  </div>
                  <span className="tech-count">({tech.count})</span>
                </div>
              ))}
              <div className="show-more" onClick={() => setShowModal(true)}>SHOW MORE</div>
            </div>

            <div className="filter-section-divider"></div>

            <div className="experience-filter">
              <h4> Years of Experience</h4>
              <div className="experience-slider">
                <div className="slider-track"></div>
                <div 
                  className="slider-fill" 
                  style={{
                    left: `${(minExperience / 30) * 100}%`,
                    width: `${((maxExperience - minExperience) / 30) * 100}%`
                  }}
                ></div>
                <input
                  type="range"
                  min="0"
                  max="30"
                  value={minExperience}
                  onChange={(e) => setMinExperience(e.target.value)}
                  className="slider-input"
                  style={{zIndex: 1}}
                />
                <input
                  type="range"
                  min="0"
                  max="30"
                  value={maxExperience}
                  onChange={(e) => setMaxExperience(e.target.value)}
                  className="slider-input"
                  style={{zIndex: 2}}
                />
              </div>
              <div className="experience-range">
                <div>
                  <div className="range-label">Min</div>
                  <select 
                    className="range-input" 
                    value={minExperience}
                    onChange={(e) => setMinExperience(e.target.value)}
                  >
                    <option value="0">0</option>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                    <option value="10">10</option>
                  </select>
                </div>
                <div>
                  <div className="range-label">Max</div>
                  <select 
                    className="range-input" 
                    value={maxExperience}
                    onChange={(e) => setMaxExperience(e.target.value)}
                  >
                    <option value="5">5</option>
                    <option value="10">10</option>
                    <option value="15">15</option>
                    <option value="20">20</option>
                    <option value="30">30</option>
                  </select>
                </div>
              </div>
            </div>

            <div className="filter-section-divider"></div>

            <div className="location-filter">
              <h4> Locations</h4>
              <input
                type="text"
                className="location-search"
                value={locationSearch}
                onChange={(e) => setLocationSearch(e.target.value)}
              />
              {locationOptions.map(location => (
                <div key={location.name} className="location-option">
                  <div style={{display: 'flex', alignItems: 'center'}}>
                    <input 
                      type="checkbox" 
                      id={location.name}
                      checked={selectedLocations.includes(location.name)}
                      onChange={() => handleLocationChange(location.name)}
                    />
                    <label htmlFor={location.name}>{location.name}</label>
                  </div>
                  <span className="tech-count">({location.count})</span>
                </div>
              ))}
            </div>
          </div>
        </aside>

        <main className="content">
          <div className="search-section">
            <input
              type="text"
              className="search-input"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder=" "
            />
            <label className="search-label">Search Keywords,Eg:(Python, Full Stack..)</label>
          </div>

          <div className="jobs-grid">
            {displayedJobs.map(job => (
              <div key={job.id} className="job-card">
                <div className="job-header">
                  <div>
                    <h3 className="job-title">{job.title}</h3>
                    <div className="job-experience">{job.experience}</div>
                  </div>
                  <button className="share-btn" onClick={() => shareJob(job.title)}></button>
                </div>

                <div className="job-tags">
                  {job.tags.map(tag => (
                    <span key={tag} className="tag">{tag}</span>
                  ))}
                </div>

                <div className="progress-bar"></div>

                <div className="auth-links">
                  <a href="#">Login</a> or <a href="#">sign up</a> to know your profile Match %
                </div>

                <div className="job-footer">
                  <div className="job-location">
                     {job.location}
                  </div>
                  <div className="job-type-badge">{job.type}</div>
                </div>
              </div>
            ))}
          </div>

          {visibleJobs < filteredJobs.length && (
            <div className="show-more-jobs">
              <button className="show-more-jobs-btn" onClick={showMoreJobs}>
                Show More
              </button>
            </div>
          )}
        </main>
      </div>

      <footer className="footer">
        <div className="footer-content">
          <div className="footer-main">
            <div className="footer-section">
              <div className="footer-logo">AugmntX</div>
              <a href="https://play.google.com/store/apps/details?id=com.augmntx.app&hl=en-US" target="_blank" rel="noopener noreferrer" className="google-play-btn">
                <img src="data:image/svg+xml;base64," alt="Get it on Google Play" />
              </a>
            </div>

            <div className="footer-section">
              <h3>Information</h3>
              <ul>
                <li><a href="#">About Us</a></li>
                <li><a href="#">Corporate Information</a></li>
                <li><a href="#">Press</a></li>
                <li><a href="#">Careers</a></li>
                <li><a href="#">Blog</a></li>
                <li><a href="#">Contact Us</a></li>
                <li><a href="#">Tech Stack</a></li>
              </ul>
            </div>

            <div className="footer-section">
              <h3>AugmntX</h3>
              <ul>
                <li><a href="#">View Profiles</a></li>
                <li><a href="#">Discover</a></li>
                <li><a href="#">On Demand Talent</a></li>
                <li><a href="#">Pricing</a></li>
                <li><a href="#">Augmentation Data</a></li>
                <li><a href="#">Status</a></li>
                <li><a href="#">Email Opt-Out</a></li>
              </ul>
            </div>

            <div className="footer-section">
              <h3>Vendor</h3>
              <ul>
                <li><a href="#">Sign Up</a></li>
                <li><a href="#">Vendor Login</a></li>
                <li><a href="#">Post Job</a></li>
                <li><a href="#">Remote Jobs</a></li>
                <li><a href="#">Resources</a></li>
              </ul>
            </div>
          </div>

          <div className="footer-bottom">
            <div> 2022 - 2025 AugmntX - Labor Omnia Vincit  by <a href="#" style={{color: '#4c51bf'}}>SuperLabs</a></div>
            <div className="footer-links">
              <a href="#">Terms of Use</a>
              <a href="#">Privacy Policy</a>
            </div>
          </div>
        </div>
      </footer>

      <button className="scroll-to-top" onClick={scrollToTop}>

      </button>

      {showModal && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3 className="modal-title">Filter & Sort</h3>
              <button className="close-btn" onClick={() => setShowModal(false)}></button>
            </div>

            <div className="modal-content">
              <div className="modal-section">
                <h4> Date Posted</h4>
                <div className="filter-options">
                  <div className="filter-option">
                    <input 
                      type="radio" 
                      id="modal-past3days" 
                      name="modalDateFilter" 
                      value="past3days"
                      checked={dateFilter === 'past3days'}
                      onChange={(e) => setDateFilter(e.target.value)}
                    />
                    <label htmlFor="modal-past3days">Past 3 Days</label>
                  </div>
                  <div className="filter-option">
                    <input 
                      type="radio" 
                      id="modal-pastweek" 
                      name="modalDateFilter" 
                      value="pastweek"
                      checked={dateFilter === 'pastweek'}
                      onChange={(e) => setDateFilter(e.target.value)}
                    />
                    <label htmlFor="modal-pastweek">Past Week</label>
                  </div>
                  <div className="filter-option">
                    <input 
                      type="radio" 
                      id="modal-pastmonth" 
                      name="modalDateFilter" 
                      value="pastmonth"
                      checked={dateFilter === 'pastmonth'}
                      onChange={(e) => setDateFilter(e.target.value)}
                    />
                    <label htmlFor="modal-pastmonth">Past Month</label>
                  </div>
                  <div className="filter-option">
                    <input 
                      type="radio" 
                      id="modal-all" 
                      name="modalDateFilter" 
                      value="all"
                      checked={dateFilter === 'all'}
                      onChange={(e) => setDateFilter(e.target.value)}
                    />
                    <label htmlFor="modal-all">All</label>
                  </div>
                </div>
              </div>

              <div className="modal-section">
                <h4> Technology / Tools</h4>
                <input
                  type="text"
                  placeholder="Technology"
                  className="tech-search"
                  value={techSearch}
                  onChange={(e) => setTechSearch(e.target.value)}
                />
                <div className="tech-grid">
                  {filteredTechOptions.map(tech => (
                    <div key={tech.name} className="tech-option">
                      <div style={{display: 'flex', alignItems: 'center'}}>
                        <input 
                          type="checkbox" 
                          id={`modal-${tech.name}`}
                          checked={selectedTech.includes(tech.name)}
                          onChange={() => handleTechChange(tech.name)}
                        />
                        <label htmlFor={`modal-${tech.name}`}>{tech.name}</label>
                      </div>
                      <span className="tech-count">({tech.count})</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            <div className="modal-footer">
              <button className="clear-btn" onClick={clearAllFilters}>Clear All</button>
              <button className="search-btn" onClick={() => setShowModal(false)}>Search</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
