import React, { useState } from 'react';

const mockJobs = [
  {
    id: 1,
    title: "Senior Frontend Developer",
    company: "TechCorp Inc.",
    type: "Full-time",
    location: "Remote",
    salary: "$80k - $120k",
    description: "We're looking for an experienced frontend developer to join our team and build amazing user experiences.",
    tags: ["React", "JavaScript", "CSS", "TypeScript"]
  },
  {
    id: 2,
    title: "Backend Engineer",
    company: "StartupXYZ",
    type: "Contract",
    location: "Remote",
    salary: "$70k - $100k",
    description: "Join our backend team to build scalable APIs and microservices that power our platform.",
    tags: ["Node.js", "Python", "AWS", "MongoDB"]
  },
  {
    id: 3,
    title: "Full Stack Developer",
    company: "Digital Agency",
    type: "Full-time",
    location: "Remote",
    salary: "$90k - $130k",
    description: "Work on diverse client projects using modern web technologies in a collaborative environment.",
    tags: ["React", "Node.js", "PostgreSQL", "Docker"]
  },
  {
    id: 4,
    title: "DevOps Engineer",
    company: "CloudTech Solutions",
    type: "Full-time",
    location: "Remote",
    salary: "$95k - $140k",
    description: "Help us build and maintain our cloud infrastructure and deployment pipelines.",
    tags: ["AWS", "Kubernetes", "Terraform", "CI/CD"]
  },
  {
    id: 5,
    title: "UI/UX Designer",
    company: "Design Studio",
    type: "Part-time",
    location: "Remote",
    salary: "$50k - $75k",
    description: "Create beautiful and intuitive user interfaces for web and mobile applications.",
    tags: ["Figma", "Sketch", "Prototyping", "User Research"]
  },
  {
    id: 6,
    title: "Data Scientist",
    company: "Analytics Pro",
    type: "Full-time",
    location: "Remote",
    salary: "$100k - $150k",
    description: "Analyze complex datasets and build machine learning models to drive business insights.",
    tags: ["Python", "Machine Learning", "SQL", "Tableau"]
  }
];

function App() {
  const [jobs, setJobs] = useState(mockJobs.slice(0, 4));
  const [searchTerm, setSearchTerm] = useState('');
  const [jobType, setJobType] = useState('');
  const [location, setLocation] = useState('');

  const loadMoreJobs = () => {
    setJobs(mockJobs);
  };

  const filteredJobs = jobs.filter(job => {
    return (
      job.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (jobType === '' || job.type === jobType) &&
      (location === '' || job.location === location)
    );
  });

  return (
    <div>
      <header className="header">
        <div className="container">
          <h1>Remote Jobs</h1>
        </div>
      </header>

      <section className="search-section">
        <div className="container">
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search jobs..."
              className="search-input"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button className="search-btn">Search</button>
          </div>

          <div className="filters">
            <select 
              className="filter-select"
              value={jobType}
              onChange={(e) => setJobType(e.target.value)}
            >
              <option value="">All Job Types</option>
              <option value="Full-time">Full-time</option>
              <option value="Part-time">Part-time</option>
              <option value="Contract">Contract</option>
            </select>

            <select 
              className="filter-select"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
            >
              <option value="">All Locations</option>
              <option value="Remote">Remote</option>
            </select>
          </div>
        </div>
      </section>

      <main className="container">
        <div className="jobs-grid">
          {filteredJobs.map(job => (
            <div key={job.id} className="job-card">
              <div className="job-header">
                <div>
                  <h3 className="job-title">{job.title}</h3>
                  <p className="company-name">{job.company}</p>
                </div>
                <span className="job-type">{job.type}</span>
              </div>

              <div className="job-details">
                <span>📍 {job.location}</span>
                <span>💰 {job.salary}</span>
              </div>

              <p className="job-description">{job.description}</p>

              <div className="job-tags">
                {job.tags.map(tag => (
                  <span key={tag} className="tag">{tag}</span>
                ))}
              </div>
            </div>
          ))}
        </div>

        {jobs.length < mockJobs.length && (
          <div className="load-more">
            <button className="load-more-btn" onClick={loadMoreJobs}>
              Load More Jobs
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
